
public class JavaBasics02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int myNum = 1;
		System.out.println(myNum); // assignment
		System.out.println(++myNum); // increment
		System.out.println(myNum); // increment changes the value of variable
		System.out.println(myNum + 1);
		System.out.println(myNum);

		int num = 1;
		System.out.println(num++);

		System.out.println(num);

		int a = 5;
		int b = 6;
		int c = ++a - b++ + a++;
		int z = ++a - b++ + a++;
		System.out.println(z);

		// ctrl + <-> <+>

	}

}
