
public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age = 12;
		while (age < 21) {
			System.out.println("probably next year, you are " + age + " now!"); // concatenation
			++age;
			System.out.println("so finally, have some cocacola");

		int num1 = 7;
		while (num1 > 3) {
			System.out.println(--num1 + "");
		}
		}

	}

}
